from PyQt5 import QtCore, QtGui, QtWidgets
import pickle
with open("/home/rithvik/House_pricing/House_Price_Prediction", "rb") as f:
    model = pickle.load(f)

def get_prediction(Neighborhood, HouseStyle, MSZoning, GrLivArea, TotalBsmtSF, GarageType, OverallQual, Utilities):

   
    ngbs = ['Bloomington Heights', 'Bluestem', 'Briardale', 'Brookside', 'Clear Creek', 'College Creek', 'Crawford', 'Edwards', 'Gilbert', 'Iowa DOT and Rail Road', 'Meadow Village', 'Mitchell', 'North Ames','Northpark Villa','Northwest Ames', 'Northridge', 'Northridge Heights', 'Old Town', 'South & West of Iowa State University', 'Sawyer', 'Sawyer West', 'Somerset', 'Stone Brook', 'Timberland', 'Veenker']
    ngb_lblEval = {}
    for i in range(25):
        ngb_lblEval[ngbs[i]] = i
        
    MSZ = {'C (all)':0, 'FV':1, 'RH':2, 'RL':3, 'RM':4}
    Utili = {'AllPub':0, 'NoSeWa':1}
    Hstyle = {'1.5Fin':0, '1.5Unf':1, '1Story':2, '2.5Fin':3, '2.5Unf':4, '2Story':5, 'SFoyer':6, 'SLvl':7}
    grgtype = {'2Types':0, 'Attchd':1, 'Basment':2, 'BuiltIn':3, 'CarPort':4, 'Detchd':5, 'No Garage':6}
    
    MSZoning = MSZ[MSZoning]
    Utilities = Utili[Utilities]
    Neighborhood = ngb_lblEval[Neighborhood]
    GarageType = grgtype[GarageType]
    HouseStyle = Hstyle[HouseStyle]
    prd_lis = [OverallQual, TotalBsmtSF, GrLivArea, MSZoning, Utilities, Neighborhood, GarageType, HouseStyle]
    prediction = model.predict([prd_lis])
    
    return (prediction[0])

