    def pressed(self):
        Neighborhood = self.comboBox_3.currentText()
        HouseStyle = self.comboBox_4.currentText()
        MSZoning = self.comboBox.currentText()
        GrLivArea = (self.plainTextEdit_2.toPlainText()).strip()
        TotalBsmtSF = (self.plainTextEdit.toPlainText()).strip()
        GarageType = self.comboBox_6.currentText()
        OverallQual = self.comboBox_5.currentText()
        Utilities = self.comboBox_2.currentText()
        try:
            ans = get_prediction(Neighborhood, HouseStyle, MSZoning, int(GrLivArea), int(TotalBsmtSF), GarageType, int(OverallQual), Utilities)
            ans_len = len(str(ans).split('.')[0])
            #print(ans_len, ans)
            if ans_len >= 6:
                self.label_16.setText(' '*13 + "Estimated Price : $"+ str(ans)[:ans_len-6+3]+','+str(ans)[ans_len-6+3:ans_len-6+6])
                self.label_16.adjustSize()
            elif ans_len >=5 and ans_len < 6:
                self.label_16.setText(' '*13 + "Estimated Price : $"+ str(ans)[:ans_len-5+2]+','+str(ans)[ans_len-5+2:ans_len-5+5])
                self.label_16.adjustSize()
            else: 
                self.label_16.setText(' '*13 + "Estimated Price : $"+ '{:.0f}'.format(ans))
                self.label_16.adjustSize()
        except:
            self.label_16.setText(' '*21 + "Invalid Entry Try Again")
            self.label_16.adjustSize()
     

